mapper.select:
SELECT pwd
FROM artist_member
WHERE pwd = #{pwd}

SELECT COUNT(*) > 0
FROM artist_member
WHERE id = #{id}

SELECT id
FROM artist_member
WHERE name = #{name}
  AND email = #{email}

mapper.update:
update artist_member
set pwd = #{pwd}
where id = #{id}

mapper.select:
select *
from artist_member
ORDER BY name ASC;

select *
from common_member
ORDER BY name ASC;
