mapper.select:
SELECT *
FROM enterprise_member
WHERE id = #{id}

SELECT pwd
FROM enterprise_member
WHERE pwd = #{pwd}

SELECT *
FROM enterprise_member
WHERE admin_num = #{adminNum}

SELECT COUNT(*) > 0
FROM enterprise_member
WHERE id = #{id}

SELECT *
FROM enterprise_member
WHERE id = #{id}

SELECT id
FROM enterprise_member
WHERE name = #{name}
  AND email = #{email}

mapper.update:
update enterprise_member
set pwd = #{pwd}
where id = #{id}

UPDATE enterprise_member
SET
    pwd = #{pwd},
    phone = #{phone},
    email = #{email}
WHERE id = #{id}

mapper.select:
SELECT id
FROM schedules
WHERE conId = #{param1}

mapper.delete:
DELETE FROM ticket
WHERE rId IN

    #{item}

DELETE FROM reservation
WHERE sId IN

    #{item}
